import './Expenses.css'
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import ExpensesFilter from './ExpensesFilter';
import './ExpensesFilter.css';
import { useState } from 'react';

const Expenses = props => {

    const [selectedDate, setSelectedDate] = useState("2020");

    const onFilterSelection = selectedYear => {
        setSelectedDate(selectedYear);
        console.log(props.selectedDate)
    }

    let filteredExpenses = props.expenses.filter(function(e){e.date.getFullYear() === selectedDate})

    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter selectedDate={selectedDate} onFilterSelection={onFilterSelection} />
                {props.filteredExpenses.map((e) => (
                    <ExpenseItem
                        key={e.id}
                        title={e.title}
                        amount={e.amount}
                        date={e.date} />
                ))}
            </Card>
        </div>
    )
}
export default Expenses;
